import { Component, Inject } from '@angular/core';
import { HttpClient, HttpHeaders } from '@angular/common/http';
import { HttpSettings, HttpService } from '../service/http.service';
import { Observable } from 'rxjs';
import { NgxFileDropEntry, FileSystemFileEntry, FileSystemDirectoryEntry } from 'ngx-file-drop';
import { SignalRService } from '../service/signalR.service';
import { ServerMsg } from '../class/serverMsg';


@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css'],
})
export class HomeComponent {

  private baseUrl: string;
  public files: NgxFileDropEntry[] = [];
  public docxContent: string = "";
  public serverMsg: ServerMsg | undefined;
  public theText:string = "";

  constructor(http: HttpClient,
    @Inject('BASE_URL') baseUrl: string,
    private httpService: HttpService,
    private signalRService: SignalRService,) {
    this.baseUrl = baseUrl;
    this.postData();
  }

  async ngOnInit() {
    this.signalRService.startConnection();
    this.signalRService.openDataListener();

    this.signalRService.onMessage().subscribe(async (message: ServerMsg) => {
      this.serverMsg = message;
      if (this.serverMsg.msgName == 'newFile') {
        this.theText = this.serverMsg.data;
      }
      console.log(this.serverMsg);
    });
  }

  getData() {
    const apiUrl = this.baseUrl + "home/bonjourget"; // Replace with your API URL

    this.httpService.xhr<any>({ url: apiUrl, method: 'GET' }).subscribe(
      (response) => {
        console.log(response);
      },
      (error) => {
        console.error('Error:', error);
      }
    );
  }

  postData() {
    const dataToSend = {
      message: 'Hello, World!',
      value: 42
    };

    const apiUrl = this.baseUrl + "home/bonjourpost"; // Replace with your API URL

    this.httpService.xhr<any>({ url: apiUrl, method: 'POST', data: "sdsdafsdf" }).subscribe(
      (response) => {
        console.log(response);
      },
      (error) => {
        console.error('Error:', error);
      }
    );
  }

  public dropped(files: NgxFileDropEntry[]) {
    this.files = files;
    console.log(files);
    for (const droppedFile of files) {

      // Is it a file?
      if (droppedFile.fileEntry.isFile) {
        const fileEntry = droppedFile.fileEntry as FileSystemFileEntry;
        fileEntry.file((file: File) => {

          this.downloadFile(file);

        });
      } else {
        // It was a directory (empty directories are added, otherwise only files)
        const fileEntry = droppedFile.fileEntry as FileSystemDirectoryEntry;
        console.log(droppedFile.relativePath, fileEntry);
      }
    }
  }

  public fileOver(event: any) {
    console.log(event);
  }

  public fileLeave(event: any) {
    console.log(event);
  }

  private downloadFile(file: File) {
    const blob = new Blob([file], { type: file.type });
    const url = window.URL.createObjectURL(blob);

    const a = document.createElement('a');
    a.style.display = 'none';
    a.href = url;
    a.download = file.name;

    document.body.appendChild(a);
    a.click();

    window.URL.revokeObjectURL(url);
  }
}
